import React, { useEffect, useState } from "react";
// import { io } from "socket.io-client";
import { socket } from "../socket";
function Chat() {
  const [connectedUsers, setConnectedUsers] = useState({});
  const [message, setMessage] = useState();
  const [msgrecived, setmsgRecived] = useState();
  const [to, setTo] = useState("");
  useEffect(() => {
    socket.on("recive_msg", (data) => {
      console.log(data.msg);
      setmsgRecived(data.msg);
    });
    socket.on("connectedUsers", (users) => {
      setConnectedUsers(users);
    });
    console.log(connectedUsers);
  }, [socket]);

  const handelChange = (e) => {
    setMessage(e.target.value);
  };
  const handelSubmit = (e) => {
    e.preventDefault();
    const username = localStorage.getItem("username");
    const email = localStorage.getItem("email");
    socket.emit("new-user-joined", email);
    socket.emit("message", { message, to, email, username });
  };
  return (
    <div>
      <input
        placeholder="Message"
        value={message}
        onChange={handelChange}
        type="text"
      />

      <input
        placeholder="Enter the E-mail you want to send"
        value={to}
        onChange={(e) => {
          setTo(e.target.value);
        }}
        type="text"
      />
      <button onClick={handelSubmit} type="submit">
        Submit
      </button>
      <ul id="test-connection">
        <li>{connectedUsers}</li>
      </ul>
      <div>{msgrecived}</div>
    </div>
  );
}

export default Chat;
