import React, { useEffect, useState } from "react";
// import { io } from "socket.io-client";
import { socket } from "../socket";
function Chat() {
  const [message, setMessage] = useState();
  // const [msgrecived, setmsgRecived] = useState();
  const [to, setTo] = useState("");
  useEffect(() => {
    socket.on("recive_msg", (data) => {
      if (data.to === localStorage.getItem("email")) {
        const mainUl = document.getElementById("test-connection");
        const node = document.createElement("li");
        const textNide = document.createTextNode(data.msg);
        node.appendChild(textNide);
        mainUl.appendChild(node);
        console.log(data.id);
      } else {
        console.log(data);
        const mainUl = document.getElementById("test-connection");
        const node = document.createElement("li");
        const textNide = document.createTextNode(data.msg);
        node.appendChild(textNide);
        mainUl.appendChild(node);
      }
      socket.emit(data.msg);
    });
  }, [socket]);

  const handelChange = (e) => {
    setMessage(e.target.value);
  };
  const handelSubmit = (e) => {
    e.preventDefault();
    const username = localStorage.getItem("username");
    const email = localStorage.getItem("email");
    socket.emit("message", { message, to, email, username });
  };
  return (
    <div>
      <input
        placeholder="Message"
        value={message}
        onChange={handelChange}
        type="text"
      />

      <input
        placeholder="Enter the E-mail you want to send"
        value={to}
        onChange={(e) => {
          setTo(e.target.value);
        }}
        type="text"
      />
      <button onClick={handelSubmit} type="submit">
        Submit
      </button>
      <ul id="test-connection"></ul>
    </div>
  );
}

export default Chat;
