import React, { useEffect, useState } from "react";
// import { io } from "socket.io-client";
import { socket } from "../socket";
function Chat() {
  const [message, setMessage] = useState();
  useEffect(() => {
    socket.on("recive_msg", (data) => {
      console.log(data);
      const mainUl = document.getElementById("test-connection");
      const node = document.createElement("li");
      node.textContent = data.message;
      mainUl.appendChild(node);
    });
  }, [socket]);

  const handelChange = (e) => {
    setMessage(e.target.value);
  };
  const handelSubmit = (e) => {
    e.preventDefault();
    socket.emit("message", { message });
  };
  return (
    <div>
      <input value={message} onChange={handelChange} type="text" />
      <button onClick={handelSubmit} type="submit">
        Submit
      </button>
      <ul id="test-connection"></ul>
    </div>
  );
}

export default Chat;
