import React, { useEffect, useState } from "react";
// import { io } from "socket.io-client";
import { socket } from "../socket";
function Chat() {
  const [connectedUsers, setConnectedUsers] = useState({});
  const [message, setMessage] = useState();
  const [msgrecived, setmsgRecived] = useState([]);
  // const [to, setTo] = useState("");
  useEffect(() => {
    let username = "";
    let email = "";
    if (!localStorage.getItem("name" && !localStorage.getItem("email"))) {
      username = prompt("Enter your Name");
      email = prompt("Enter your email");
    }
    localStorage.setItem("name", username);
    localStorage.setItem("email", email);
    const user = {
      userName: localStorage.getItem("name"),
      email: localStorage.getItem("email"),
    };
    // Emit the 'join' event to send user information to the server
    socket.emit("join", user);

    // Listen for 'connectedUsers' event to receive the list of connected users
    socket.on("connectedUsers", (users) => {
      setConnectedUsers(users);
    });
    socket.on("recive_msg", (data) => {
      setmsgRecived([data]);
      console.log(data.msg, "jhsgdfjhghgj", data.author);
    });
  }, [socket]);

  const handelChange = (e) => {
    setMessage(e.target.value);
  };
  const handelSubmit = (e) => {
    e.preventDefault();
    const author = localStorage.getItem("name");
    socket.emit("message", { message, author });
    console.log(msgrecived, "from msg recived");
  };
  return (
    <div>
      <input
        placeholder="Message"
        value={message}
        onChange={handelChange}
        type="text"
      />
      <button onClick={handelSubmit} type="submit">
        Submit
      </button>
      <button
        onClick={() => {
          socket.disconnect();
        }}
        type="submit"
      >
        DisConnect
      </button>
      <ul id="test-connection">
        {Object.keys(connectedUsers).map((userId) => (
          <li key={userId}>
            <strong>Name:</strong> {connectedUsers[userId].userName}, -----
            {connectedUsers[userId].socketId}
            -- {}
          </li>
        ))}
      </ul>
      {/* <div>{msgrecived}</div> */}
      <div
        onClick={() => {
          console.log(msgrecived);
        }}
      >
        <div>{msgrecived}</div>
      </div>
    </div>
  );
}

export default Chat;
