import React, { useEffect, useState } from "react";
// import { io } from "socket.io-client";
import { socket } from "../socket";
function Chat() {
  const [message, setMessage] = useState();
  // const [msgrecived, setmsgRecived] = useState();
  const [userEmail, setUserEmail] = useState();
  const [to, setTo] = useState("");
  useEffect(() => {
    socket.on("recive_msg", (data) => {
      const user = localStorage.getItem("username");
      if (data.to === user) {
        const mainUl = document.getElementById("test-connection");
        const node = document.createElement("li");
        const textNide = document.createTextNode(data.message);
        node.appendChild(textNide);
        mainUl.appendChild(node);
      }
      console.log(data.id);
    });
  }, [socket]);

  const handelChange = (e) => {
    setMessage(e.target.value);
  };
  const handelSubmit = (e) => {
    e.preventDefault();
    socket.emit("message", { message, to });
    localStorage.setItem("username", userEmail);
  };
  return (
    <div>
      <input value={message} onChange={handelChange} type="text" />
      <input
        value={userEmail}
        onChange={(e) => {
          setUserEmail(e.target.value);
        }}
        type="text"
        placeholder="Resever E-mail"
      />
      <input
        placeholder="Your E-mail"
        value={to}
        onChange={(e) => {
          setTo(e.target.value);
        }}
        type="text"
      />
      <button onClick={handelSubmit} type="submit">
        Submit
      </button>
      <ul id="test-connection"></ul>
    </div>
  );
}

export default Chat;
